/// <reference types="node" />
import { EventEmitter } from "events";
export default class Actions extends EventEmitter {
    map: {
        [key: string]: any;
    };
    register(action: string): this;
    getAll(): string[];
    is(action: string): boolean;
    down(action: string, options: any): void;
    change(action: string, options: any): void;
    up(action: string, options: any): void;
}
